insert into `message_template` (`id`,`obj_vesion`,`date_created`,`message_type`,`template`,`notification_type`,`language`) values (18,0,'2009-10-06 00:00:00','TEXT','How are U feeling this week?Baby is growing.He or she may be moving now& U will be able to feel it with time.Pregnancy is nice but there are some things U must get ready.Start by putting down some money each week for things for the baby. You don''t need too much.Important things are baby napkins,dresses for U&baby,cot sheets sewn from aligudon,soap,towels,powder,shea butter,disenfectant like Camel & sanitary towels for U.*From Mobile Midwife*.',21,1);
