insert into `message_template` (`id`,`obj_vesion`,`date_created`,`message_type`,`template`,`notification_type`,`language`) values (31,0,'2009-10-06 00:00:00','TEXT','Your baby can hear U now so talk&have your husband talk so baby will know your voices&feel bonded with U.Think about baby''s arrival&your delivery.Do U have transport to hospital or clinic in an emergency?Should U move closer to where U will deliver to save on transport?Have you been to ANC&know your midwifes phone number.Remember all births are safer at a health facility&if it is your 1st baby it is important to be at a clinic or hospital.*From Mobile Midwife*.',34,1);
