insert into `message_template` (`id`,`obj_vesion`,`date_created`,`message_type`,`template`,`notification_type`,`language`) values (40,0,'2009-10-06 00:00:00','TEXT','Soon baby will join us in the outside world. Baby will need to be protected from infection&sickness.To help baby- BREASTFEED.Breastmilk is perfect food for babies&gives all they need to grow big&strong&protects baby from sickness.Feed baby from breast as soon as born.First milk is thick,yellow&the BEST!Feed breastmilk ONLY.No water or food for 6 months.Feed baby 8-12 times/day even if baby sleeps alot.Never fear asking midwife for help.*From Mobile Midwife*.',43,1);
