insert into `message_template` (`id`,`obj_vesion`,`date_created`,`message_type`,`template`,`notification_type`,`language`) values (42,0,'2009-10-06 00:00:00','TEXT','Babies get food&oxygen in womb through placenta(or afterbirth).Placenta passes nutrients from mum to baby through umbilical cord attached to baby''s belly.After birth,cord is cut&a stump remains that soon falls off.Care of the stump before it falls off is very important.Keep it clean&dry.Dont apply anything to it.No spirit,ash,glass or anything.Make sure baby napkins dont rub or cover stump.Dont force stump to fall,it will go in 10-21 days.*From Mobile Midwife*.',45,1);
