insert into `message_template` (`id`,`obj_vesion`,`date_created`,`message_type`,`template`,`notification_type`,`language`) values (210,0,'2010-02-02 00:00:00','TEXT','Newborns only need a few things to be happy.Food,love,sleep&a clean nappy.Changing baby''s nappy often is important-bacteria in urine&faeces can make skin sore&give rashes.This happens very quick in warm weather.Expect to change nappy before or after every feed.This should every 1-3hrs.Baby is well fed if you change 6-10 nappies/day.Clean baby''s buttocks with soft,damp,clean cloth&be sure to wash hands with soap after every change*From Mobile Midwife*.',57,1);
