insert into `message_template` (`id`,`obj_vesion`,`date_created`,`message_type`,`template`,`notification_type`,`language`) values (228,0,'2010-02-02 00:00:00','TEXT','Malaria is very dangerous.It can cause low blood in the mother+miscarriage.Help yourself by sleeping under insecticide treated nets.Soon you will be given medicine called SP at the clinic to protect you.Wear long clothes in light colors in the evening.Try burning neem leaves or orange peels to keep mosquitos away or you can spray the rooms.Avoid hanging many clothes in your room,mosquitos hide in them.Fold your clothes up.Go to the clinic if you feel cold or have headache.',63,1);
