insert into `message_template` (`id`,`obj_vesion`,`date_created`,`message_type`,`template`,`notification_type`,`language`) values (348,0,'2010-04-30 00:00:00','TEXT','If U are pregnant&feel like hiding it for now,its normal.But remember the evil eye does not bring problems in pregnancy,most problems are because of things that can be treated if U go to clinic early.If U miss a period or think U are pregnant go to clinic NOW!Dont fear-midwives want to make sure U stay healthy&have a healthy baby.They will keep your secret.U get National Health Insurance free for 1year when pregnant so register now.*From Mobile Midwife*',104,1);
