insert into `message_template` (`id`,`obj_vesion`,`date_created`,`message_type`,`template`,`notification_type`,`language`) values (372,0,'2010-04-30 00:00:00','TEXT','Malaria is very dangerous.It can cause low blood in the mother&miscarriage-where you lose the baby.Help yourself by sleeping under insecticide treated nets.Soon U will be given a drug calleed SP at clinic to protect U from malaria.Wear long clothes in light colors in the evening.Try burning neem leaves or orange peels to keep mosquitos away or spray the room.Go to the clinic STRAIGHT AWAY if U are feeling cold or have headache.*From Mobile Midwife*',112,1);
