insert into `message_template` (`id`,`obj_vesion`,`date_created`,`message_type`,`template`,`notification_type`,`language`) values (405,0,'2010-04-30 00:00:00','TEXT','As your muscles are stretching to fit your growing baby U may start seeing stretch marks.U may also notice a dark line running up your belly.This line fades after birth.Your nipples may be getting darker too but this will also fade after birth.For stretch marks,try not to gain weight too fast,eat healthy&rub your skin with shea butter.Eating groundnuts,cashews,coconut &peas might also help.*From Mobile Midwife*',123,1);
