insert into `message_template` (`id`,`obj_vesion`,`date_created`,`message_type`,`template`,`notification_type`,`language`) values (439,0,'2010-04-30 00:00:00','TEXT','U are nearly 4 months pregnant now.If U havent been to antenatal care (ANC) yet U need to go this week.Its free&best way to make sure U&baby are healthy.U need to join National Health Insurance at ANC,also free for pregnant women.At ANC you will be offered lab tests to check your blood for anaemia (low blood level) infections like syphilis,hepatitis&HIV/AIDS.Testing is important so treatment can be started for a healthy pregnancy & baby.*From Mobile Midwife*',134,1);
