insert into `message_template` (`id`,`obj_vesion`,`date_created`,`message_type`,`template`,`notification_type`,`language`) values (460,0,'2010-04-30 00:00:00','TEXT','Its good to involve your husband/family as U prepare for baby''s birth.Some tips: 1.know where nearest hospital/clinic is&plan how to get there quick in emergency. 2.All births are safer in a health facility with medical professionals,plan with your midwife 3.know your midwife''s phone number for emergency 3.Put down small money weekly for things needed at delivery-baby napkins,soap,antiseptic,cot sheets,clean clothes for baby&towels.*From Mobile Midwife*',141,1);
