insert into `message_template` (`id`,`obj_vesion`,`date_created`,`message_type`,`template`,`notification_type`,`language`) values (490,0,'2010-04-30 00:00:00','TEXT','Don''t worry if U find yourself belching lots or feeling bloated.This is normal in pregnancy.Your womb is growing&pushing on your stomach which causes bloating,wind,burping&discomfort in your stomach.Try to eat smaller meals more often through the day.Take time eating&chew your food well.Also wear loose clothes that are not tight around your belly.*From Mobile Midwife*',151,1);
