insert into `message_template` (`id`,`obj_vesion`,`date_created`,`message_type`,`template`,`notification_type`,`language`) values (499,0,'2010-04-30 00:00:00','TEXT','For baby to grow well U must eat well. U may have been told not to eat some foods but there is no proof they will harm your baby. 3 food groups are energy giving,body building&protective foods.Try to eat items from each group at meals.Energy foods are maize,grains,oats,plantain,cassava,yams.Body building foods are all beans&peas,egushie groundnut,meat,chicken,fowl,fish.Protective foods are greens,okro,tomatoes,garden eggs&all fruits too.*From Mobile Midwife*',154,1);
