insert into `message_template` (`id`,`obj_vesion`,`date_created`,`message_type`,`template`,`notification_type`,`language`) values (571,0,'2010-04-30 00:00:00','TEXT','Message for MEN! Now it is good to start preparing for baby''s arrival.Talk to midwife&see delivery room if possible.Help your pregnant lady prepare by thinking of transport&how to pay.Who&how to call in emergency?Where will mum deliver?Who can donate blood if needed?Have U registered for NHIS yet?Put down small money for baby napkins,soap,antiseptic,mackintosh,towels,aligudon for baby.*From Mobile Midwife*',178,1);
