insert into `message_template` (`id`,`obj_vesion`,`date_created`,`message_type`,`template`,`notification_type`,`language`) values (577,0,'2010-04-30 00:00:00','TEXT','If you haven''t registered for National Health Insurance yet,do it NOW.Its free for 1year for pregnant women&covers maternity bills including cost of surgery if needed.It covers baby for first 6weeks of life also.Baby is only covered for 6weeks,so remember to register baby once born.Go to NHIS office to register as soon as U know you are pregnant,or take your wife or relative that is pregnant.*From Mobile Midwife*',180,1);
