insert into `message_template` (`id`,`obj_vesion`,`date_created`,`message_type`,`template`,`notification_type`,`language`) values (580,0,'2010-04-30 00:00:00','TEXT','Baby has grown so much that now the space for him to move around is small.U may be feeling very tired now so try to get enough rest.Is your back paining from the weight of baby?Try sitting on a mat on the floor with your back against the wall&legs spread out.Try to keep active as it will keep U strong for labour,but avoid lifting heavy objects like water,firewood&other children.*From Mobile Midwife*',181,1);
