insert into `message_template` (`id`,`obj_vesion`,`date_created`,`message_type`,`template`,`notification_type`,`language`) values (586,0,'2010-04-30 00:00:00','TEXT','For MEN! Your woman is like mother earth&the baby she is carrying is like a seed.Nurture&nourish mother earth,care for her to help the seed to grow strong.Remember pregnancy is a gift so take care of it-U may be having the next president of Ghana!Financial support can be hard,but U can support her by helping with the housework&make sure she has NHIS.Show love by going with her to ANC&learn what can help her have a healthy baby.*From Mobile Midwife*',183,1);
