insert into `message_template` (`id`,`obj_vesion`,`date_created`,`message_type`,`template`,`notification_type`,`language`) values (621,0,'2010-04-30 00:00:00','TEXT','Blood pressure checks thru pregnancy are important.High pressure can be pre-eclampsia,a serious condition that reduces blood flow to baby.Blood carries oxygen&food to baby&proper flow is important for baby''s health.If your pressure stays high U need urine test to check for pre-eclampsia.Signs of pre-eclampsia are:high blood pressure,swelling of hands&face,headaches,dizziness.Go to clinic for regular checks to make sure U&baby are healthy.*From Mobile Midwife*',195,1);
