insert into `message_template` (`id`,`obj_vesion`,`date_created`,`message_type`,`template`,`notification_type`,`language`) values (636,0,'2010-04-30 00:00:00','TEXT','Babies who are breastfed are healthier&better nourished than those given other drinks&food.Baby needs no other food or drink for first 6 months of life.Breastmilk alone meets all their needs including water.Right position for breastfeeding is:baby''s body is turned to face mum belly to belly.Baby''s chin touchs mum''s breast,more of dark skin around mum''s nipple is seen above baby''s mouth than below,baby takes long deep sucks&mum has no pain.*From Mobile Midwife*',200,1);
