insert into `message_template` (`id`,`obj_vesion`,`date_created`,`message_type`,`template`,`notification_type`,`language`) values (645,0,'2010-04-30 00:00:00','TEXT','Baby can come anytime now!Great!Do U have all U need for delivery packed in a bag?U need baby napkins,cloths,soap,Camel,baby soap,shea butter,Aligudon for baby,towels&Makintosh.Do U have midwife''s phone number?Where are U delivering?How will U get there?If U dont deliver at hospital U must report to clinic as soon as possible after the birth.This is to be sure U&baby have a great start at taking care of yourself&the blessing God has given U.*From Mobile Midwife*',203,1);
