insert into `message_template` (`id`,`obj_vesion`,`date_created`,`message_type`,`template`,`notification_type`,`language`) values (648,0,'2010-04-30 00:00:00','TEXT','When baby arrives remember they can get sick easy.Immunizations are important to protect baby.Visit clinic within 1 day of birth for checkup.Baby will get 2 jabs to protect them.Feed baby only breastmilk.Breastmilk has water so no need to give baby water.Wash hands with soap or ash&water to remove germs,infections&protect from worms.Wash before touching baby,before&after changing baby,toileting,cooking&eating to keep whole family healthy.*From Mobile Midwife*',204,1);
