insert into `message_template` (`id`,`obj_vesion`,`date_created`,`message_type`,`template`,`notification_type`,`language`) values (654,0,'2010-04-30 00:00:00','TEXT','If baby has arrived,congrats!!If not it will be here soon.Be ready to go to health facility as soon as labour starts-delivering there is safer for mum&baby.When baby is born take baby to clinic within 1 day of birth for check by nurse.Also take baby to facility STRAIGHT if they have fever,high temperature,trouble breathing,if baby is not sucking as before,wont feed,not crying or weak&limp.Ask a doctor for help before its too late*From Mobile Midwife*',206,1);
