insert into `message_template` (`id`,`obj_vesion`,`date_created`,`message_type`,`template`,`notification_type`,`language`) values (672,0,'2010-04-30 00:00:00','TEXT','Malaria is very dangerous for pregnant women&more for new babies.U&baby should sleep under insecticide treated nets.Cover baby''s arms,legs&feet in evening&at dawn.Try burning neem leaves or orange peels to keep mosquitos away or spray rooms.Clearing bushes around house&covering water containers helps too.If baby has fever go to clinic straight for early treatment that can save baby''s life.*From Mobile Midwife*',212,1);
