insert into `message_template` (`id`,`obj_vesion`,`date_created`,`message_type`,`template`,`notification_type`,`language`) values (687,0,'2010-04-30 00:00:00','TEXT','Every child born needs to be registered with National Birth&Death Registry.A Birth certificate is a legal document that functions as an eye witness account of the birth of your baby.It is needed for processing several legal documents in life like passport.U can register with representative at hospital or for the community or ask your midwife where to register.Register as soon as child is born.Registration is free.*From Mobile Midwife*',217,1);
